// import Link from 'next/link';
// import Layout from './container/common/layout';
import css from '../styles/index.css';
// import Image from 'next/image';

const Index = () => (
  <div class="container" className={css.container}>
    <Layout>
      <section>
        <nav class="nav" className={css.nav}>
          <div>
            style=
            {{
              backgroundImage:
                'url(' + `${require('../images/main/main.jpg')}` + ')',
              width: '100%',
              height: [410],
              backgroundRepeat: 'no-repeat',
              backgroundSize: 'cover',
            }}
          </div>
          {/* <Image
            src="/main/main.jpg"
            alt="Picture of the factory"
            height={410}
            width={1500}
          /> */}
          <span className={css.span}>
            <h1>MK Co., Ltd.</h1>
          </span>
        </nav>
        <main className={css.main}>
          <h2>What We Do</h2>
          <p>
            저희 엠케이는 경북 영천시에 소재하고 있고 2019년 1월 21일에 설립되어
          </p>
          <p>
            소프트웨어 개발 및 제작과 자동차 부품을 가공하여 공급하고 있습니다.
          </p>
          <p>
            또 4차 산업시대에 발맞춰 부품 생산 자동화 시스템을 개발하려는 등
            끊임없는 도전을 하고 있습니다.
          </p>
        </main>
      </section>
    </Layout>
  </div>
);

export default Index;
